import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'; 
import './App.css';
import Login from './Login';
import Register from './Register';
import FullArticle from './components/FullArticle'; 

function App() {
  const [articles, setArticles] = useState([]);
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [error, setError] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  
  const [newArticle, setNewArticle] = useState({
    title: '',
    content: '',
    author: '',
    image: '',
  });

  useEffect(() => {
    if (token) {
      fetch('http://localhost:3000/articles', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => response.json())
        .then((data) => {
          setArticles(data);
          setError(null);
        })
        .catch((error) => {
          console.error('Error fetching articles:', error);
          setError('Failed to load articles. Please try again later.');
        });
    }
  }, [token]);

  const handleLogout = () => {
    setToken('');
    localStorage.removeItem('token');
  };

  const toggleRegister = () => setShowRegister((prev) => !prev);

  // Handle input changes for the new article form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewArticle((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission for creating new article
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/articles', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newArticle),
      });
      const data = await response.json();
      if (response.ok) {
        setArticles((prev) => [data.article, ...prev]); // Add new article to state
        setNewArticle({ title: '', content: '', author: '', image: '' }); // Reset form
      } else {
        setError('Failed to create article.');
      }
    } catch (error) {
      console.error('Error posting article:', error);
      setError('Error posting article.');
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1 className="news-site-title">CBN News</h1>
          <nav className="navbar">
            <a href="/" className="nav-link">Home</a>
            <a href="/world" className="nav-link">World</a>
            <a href="/politics" className="nav-link">Politics</a>
            <a href="/business" className="nav-link">Business</a>
            <a href="/technology" className="nav-link">Technology</a>
          </nav>
        </header>

        <main className="main-content">
          {!token ? (
            <>
              <Login
                setToken={(token) => {
                  setToken(token);
                  localStorage.setItem('token', token);
                }}
              />
              <button onClick={toggleRegister}>Register</button>
              {showRegister && <Register />}
            </>
          ) : (
            <>
              <button onClick={handleLogout}>Logout</button>
              <section className="news-section">
                <h2>Latest Articles</h2>

                {/* Add a form to create new articles */}
                <form onSubmit={handleSubmit} className="new-article-form">
                  <input
                    type="text"
                    name="title"
                    placeholder="Title"
                    value={newArticle.title}
                    onChange={handleInputChange}
                    required
                  />
                  <textarea
                    name="content"
                    placeholder="Content"
                    value={newArticle.content}
                    onChange={handleInputChange}
                    required
                  />
                  <input
                    type="text"
                    name="author"
                    placeholder="Author"
                    value={newArticle.author}
                    onChange={handleInputChange}
                    required
                  />
                  <input
                    type="text"
                    name="image"
                    placeholder="Image URL (optional)"
                    value={newArticle.image}
                    onChange={handleInputChange}
                  />
                  <button type="submit">Post Article</button>
                </form>

                {error && <p className="error-message">{error}</p>}
                {articles.length > 0 ? (
                  articles.map((article) => (
                    <div key={article._id} className="news-article">
                      <h3>{article.title}</h3>
                      <p className="news-summary">{article.content.slice(0, 150)}...</p>
                      <p className="news-author"><strong>Author:</strong> {article.author}</p>
                      <p className="news-date">
                        <strong>Published:</strong> {new Date(article.createdAt).toLocaleDateString()}
                      </p>
                      <Link to={`/article/${article._id}`} className="read-more">Read more</Link>
                    </div>
                  ))
                ) : (
                  <p>Loading articles...</p>
                )}
              </section>
            </>
          )}
        </main>

        <footer className="App-footer">
          <p>&copy; 2024 CBN News. All rights reserved.</p>
        </footer>
      </div>

      <Routes>
        <Route path="/article/:id" element={<FullArticle />} />
      </Routes>
    </Router>
  );
}

export default App;
