import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams to fetch the ID from URL

const FullArticle = () => {
  const [article, setArticle] = useState(null);
  const { id } = useParams(); // Get the article ID from the URL

  useEffect(() => {
    const fetchArticle = async () => {
      const response = await fetch(`http://localhost:3000/articles/${id}`);
      const data = await response.json();
      setArticle(data); // Set article data to state
    };

    fetchArticle();
  }, [id]);

  return (
    <div className="full-article">
      {article ? (
        <>
          <h2>{article.title}</h2>
          <img src={article.image} alt={article.title} /> {/* Display the article image */}
          <p>{article.content}</p> {/* Full content */}
          <p><strong>Author:</strong> {article.author}</p>
          <p><strong>Published:</strong> {new Date(article.createdAt).toLocaleDateString()}</p>
        </>
      ) : (
        <p>Loading article...</p>
      )}
    </div>
  );
};

export default FullArticle;
